mapper.select:
SELECT
oId, oName,
(SELECT valueName FROM `datastatus` WHERE typeCode='OPERATOR_DEPARTMENTS' AND valueId=oDepartment) oDepartment,
(SELECT valueName FROM `datastatus` WHERE typeCode='OPERATOR_PERRMISSION_LEVEL' AND valueId=oStatus) oStatus,
(SELECT valueName FROM `datastatus` WHERE typeCode='WAGE_SCALE' AND valueId=oSalary) oSalary,
(SELECT valueName FROM `datastatus` WHERE typeCode='OPERATOR_WORK_STATUS' AND valueId=oWorkStatus) oWorkStatus,
`oTel`, `oSFZ`, `oAddress`, `oGender`, `oCreateTime`, `oHotelUserNum`
FROM `operator`

     oName like concat('%',#{oName},'%')
    and oDepartment=#{oDepartment}
    and oWorkStatus=#{oWorkStatus}
    and oGender=#{oGender}
    and oHotelUserNum=#{oHotelUserNum}

mapper.update:
update Operator
oName = #{oName}, oDepartment = #{oDepartment}, oStatus = #{oStatus}, oSalary = #{oSalary}, oWorkStatus = #{oWorkStatus}, oTel = #{oTel}, oSFZ = #{oSFZ}, oGender = #{oGender}, oAddress = #{oAddress}
where oId = #{oId}
